package ru.spb.devpractices.masterbest.serverside.controllers;import lombok.extern.slf4j.Slf4j;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import ru.spb.devpractices.masterbest.serverside.dto.IntegrationPushOrderDTO;import ru.spb.devpractices.masterbest.serverside.services.IntegrationPushOrderService;@RestController@RequestMapping("/integration")@Slf4jpublic class IntegrationController {    private final IntegrationPushOrderService pushOrderService;    public IntegrationController(IntegrationPushOrderService pushOrderService) {        this.pushOrderService = pushOrderService;    }    @RequestMapping(value = "/pushOrder",            method = RequestMethod.POST,            produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<?> pushOrder(@RequestBody IntegrationPushOrderDTO pushDTO){        log.info("Получен POST запрос " + pushDTO.toString());         return pushOrderService.proceedPush(pushDTO);    }}