package ru.spb.devpractices.masterbest.serverside.controllers;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import ru.spb.devpractices.masterbest.serverside.dto.UserDTO;import ru.spb.devpractices.masterbest.serverside.mappers.UserMapper;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.services.model.UserService;import java.util.List;import java.util.stream.Collectors;@RestController@RequestMapping ("users")public class UserController {    private final UserService userService;    private final UserMapper mapper;    public UserController(UserService userService, UserMapper mapper) {        this.userService = userService;        this.mapper = mapper;    }    @GetMapping("{id}")    public UserDTO getUser (@PathVariable Long id) {        User user = userService.getUserById(id);        UserDTO userDTO = mapper.toDto(user);        return userDTO;    }    @GetMapping    public List<UserDTO> getAllUsers () {        return userService.getAllUsers().stream()                .map(user -> mapper.toDto(user))                .collect(Collectors.toList());    }}