package ru.spb.devpractices.masterbest.serverside.mappers;import ru.spb.devpractices.masterbest.serverside.dto.IntegrationPushOrderDTO;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.model.order.Order;import java.util.HashSet;import java.util.stream.Collectors;import java.util.stream.Stream;public class IntegrationPushOrderMapper {    public Order toEntity(IntegrationPushOrderDTO dto, User user){        Order order = new Order();        order.setIkea_number(dto.getOrderNumber());        order.setComment(dto.getComment());        order.setUsers(Stream.of(user).collect(Collectors.toCollection(HashSet::new)));        return order;    }}