package ru.spb.devpractices.masterbest.serverside.mappers;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import ru.spb.devpractices.masterbest.serverside.dto.UserDTO;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.model.UserRole;@Mapper(componentModel = "spring")public interface UserMapper {    UserDTO toDto (User user);    User toEntity (UserDTO dto);    default  String map (UserRole role){        return role.getDescription();    }    default  UserRole map (String role){        return UserRole.getRoleByDescription(role);    }}