package ru.spb.devpractices.masterbest.serverside.model;import org.springframework.security.core.GrantedAuthority;import java.util.Arrays;public enum UserRole  implements GrantedAuthority {    USER ("Пользователь"),    ADMIN("Администратор"),    ;    private String description;    UserRole(String decsr) {        this.description = decsr;    }    @Override    public String getAuthority() {        return name();    }    public String getDescription() {        return description;    }    public static UserRole getRoleByDescription (String description){        return Arrays.stream(values()).filter(userRole -> {            return userRole.getDescription().equalsIgnoreCase(description);        }).findFirst().get();    }}