package ru.spb.devpractices.masterbest.serverside.model;import lombok.Data;import lombok.ToString;import org.hibernate.annotations.CreationTimestamp;import javax.persistence.*;import java.io.Serializable;import java.time.LocalDateTime;@ToString@Entity@Table(name ="user_status_data")@Datapublic class UserStatusData implements Serializable {    @Id    @GeneratedValue (strategy = GenerationType.AUTO)    @Column(name = "id")    private Long id;    @CreationTimestamp    @Column(name="timestamp", nullable = false, updatable = false, insertable = false)    private LocalDateTime timestamp;    @Column(name = "user_id")    private String userId;    @Enumerated (EnumType.STRING)    @Column(name = "current_status")    private UserStatus currentStatus;    @Enumerated (EnumType.STRING)    @Column(name = "future_status")    private UserStatus futureStatus;    @Column (name = "comment")    private String comment;}