package ru.spb.devpractices.masterbest.serverside.model;import lombok.Data;import org.hibernate.annotations.CreationTimestamp;import javax.persistence.*;import java.time.LocalDateTime;@Entity@Table(name ="user_status_data")@Datapublic class UserStatusData {    @Id    @GeneratedValue (strategy = GenerationType.AUTO)    @Column(name = "id")    private Long id;    @CreationTimestamp    @Column(name="timestamp", nullable = false, updatable = false, insertable = false)    private LocalDateTime timestamp;    @Column(name = "user_id")    private String userId;    @Enumerated (EnumType.STRING)    @Column(name = "current_status")    private UserStatus current_status;    @Enumerated (EnumType.STRING)    @Column(name = "future_status")    private UserStatus future_status;    @Column (name = "comment")    private String comment;}