package ru.spb.devpractices.masterbest.serverside.model.order;import lombok.Data;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;import java.math.BigDecimal;@Entity@Data@Table(name ="ikea_articles")public class IkeaArticle {    @Id    @Column(name ="article")    private String article;    @Column(name ="name")    private String name;    @Column(name ="price")    private BigDecimal price;        @Column(name ="frame")    private Boolean frame;}