package ru.spb.devpractices.masterbest.serverside.model.order;import lombok.Data;import javax.persistence.*;import java.io.Serializable;@Entity@Data@Table(name = "order_items")public class OrderItem implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    @Column (name = "id")    private Long id;    @Column(name = "article")    private String article;    @Column(name = "count")    private Integer count;    @Column (name = "name")    private String name;    @Column (name = "frame")    private Boolean isFrame = false;}