package ru.spb.devpractices.masterbest.serverside.model.order;import lombok.Data;import javax.persistence.*;import java.io.Serializable;@Entity@Data@Table(name = "order_items")public class OrderItem implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @OneToOne(fetch = FetchType.EAGER)    private IkeaArticle article;    @Column(name = "count")    private Integer count;}