package ru.spb.devpractices.masterbest.serverside.model.order;import java.util.Arrays;public enum OrderStatus {    New ("Новый", false, false),    Viewed ("Просмотрен",false, false),    Accepted ("Принят",false, false),    InWork ( "В работе",false, false),    Arrived ( "Прибыл",false, false),    Suspended("Приостановлен",false, false),    Rejected("Отклонен",true, true),    Completed("Завершен",true, true),    FalseVisit("Ложный вызов",true, true),    NotFinished("Сборка приостановлена",true, true),    CancelledByOperator ("Отменен диспетчером", true, true)    ;    private Boolean isTerminal;    private Boolean isHistoryStatus;    private String description;    OrderStatus(String description, boolean isHistoryStatus, boolean isTerminal) {        this.description = description;        this.isHistoryStatus = isHistoryStatus;        this.isTerminal = isTerminal;    }    public Boolean isHistoryStatus() {        return isHistoryStatus;    }    public String getDescription() {        return description;    }    public static OrderStatus getStatusByDescription (String description){        return Arrays.stream(values()).filter(status -> {            return status.getDescription().equalsIgnoreCase(description);        }).findFirst().get();    }    public Boolean isTerminalStatus() {        return isTerminal;    }}