package ru.spb.devpractices.masterbest.serverside.model.order;import java.util.Arrays;public enum OrderStatus {    New ("Новый", false),    Viewed ("Просмотрен",false),    Accepted ("Принят",false),    InWork ( "В работе",false),    Suspended("Приостановлен",false),    Rejected("Отклонен",true),    Completed("Завершен",true),    FalseVisit("Ложный вызов",true),    NotFinished("Сборка приостановлена",true),    CancelledByOperator ("Отменен диспетчером", true)    ;    private Boolean isHistoryStatus;    private String description;    OrderStatus(String description, boolean isHistoryStatus) {        this.description = description;        this.isHistoryStatus = isHistoryStatus;    }    public Boolean isHistoryStatus() {        return isHistoryStatus;    }    public String getDescription() {        return description;    }    public static OrderStatus getStatusByDescription (String description){        return Arrays.stream(values()).filter(status -> {            return status.getDescription().equalsIgnoreCase(description);        }).findFirst().get();    }}