package ru.spb.devpractices.masterbest.serverside.model.order;import lombok.Data;import org.hibernate.annotations.CreationTimestamp;import ru.spb.devpractices.masterbest.serverside.model.User;import javax.persistence.*;import java.io.Serializable;import java.time.LocalDateTime;@Entity@Data@Table(name = "push_orders_info" )public class PushOrderInfo implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @Column(name = "user_id")    private Long user_id;    @Column(name = "order_id")    private String order_number;    @Enumerated(EnumType.STRING)    @Column(name ="order_status")    private OrderStatus orderStatus = OrderStatus.New;    @Column(name ="comment")    private String comment;    @Column(name = "cancelled")    private Boolean cancelled;    @CreationTimestamp    @Column(name="timestamp", nullable = false, updatable = false, insertable = false)    private LocalDateTime timestamp;}