package ru.spb.devpractices.masterbest.serverside.model.order;import lombok.Data;import org.hibernate.annotations.CreationTimestamp;import javax.persistence.*;import java.io.Serializable;import java.time.LocalDateTime;@Entity@Data@Table(name = "push_orders_info" )public class PushOrderInfo implements Serializable {    @Id    @Column(name = "guid")    private String guid;    @Column(name = "user_id")    private Long userId;    @Column(name ="comment")    private String comment;    @Column(name = "cancel")    private Boolean cancel;    @Enumerated(EnumType.STRING)    @Column(name ="order_status")    private OrderStatus orderStatus = OrderStatus.New;    @CreationTimestamp    @Column(name="timestamp", nullable = false, updatable = false, insertable = false)    private LocalDateTime timestamp;}