package ru.spb.devpractices.masterbest.serverside.properties;import lombok.Data;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.PropertySource;import org.springframework.stereotype.Component;@Component@PropertySource("classpath:global.properties")@Datapublic class GlobalProperties {    @Value("${server1C-path}")    private String server1CPath;    @Value("${get-orders-url}")    private String getOrderURL;    @Value("${set-order-status-url}")    private String setOrderStatusUrl;    @Value("${set-user-status-url}")    private String setUserStatusUrl;}