package ru.spb.devpractices.masterbest.serverside.services;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.http.*;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;import ru.spb.devpractices.masterbest.serverside.dto.IntegrationOrderStatusHistoryDTO;import ru.spb.devpractices.masterbest.serverside.model.order.OrderStatusHistory;import ru.spb.devpractices.masterbest.serverside.properties.GlobalProperties;@Servicepublic class IntegrationRestService {    @Autowired    private RestTemplate restTemplate;    @Autowired    private GlobalProperties properties;    @Bean    public RestTemplate restTemplate() {        return new RestTemplate();    }    public OrderStatusHistory setOrderStatus(OrderStatusHistory entity){        IntegrationOrderStatusHistoryDTO dto = new IntegrationOrderStatusHistoryDTO();        dto.setOrderNumber("1111");        dto.setStatus("Viewed");        dto.setUserID("260");        dto.setComment("comment");        HttpEntity<IntegrationOrderStatusHistoryDTO> request =                new HttpEntity(dto);        ResponseEntity<IntegrationOrderStatusHistoryDTO> response = restTemplate                .exchange(                        properties.getServer1CPath()+properties.getSetStatusURL(),                        HttpMethod.POST,                        request,                        IntegrationOrderStatusHistoryDTO.class);        return null;    }}