package ru.spb.devpractices.masterbest.serverside.services.model;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.model.order.Order;import ru.spb.devpractices.masterbest.serverside.model.order.OrderStatus;import ru.spb.devpractices.masterbest.serverside.model.order.OrderStatusHistory;import ru.spb.devpractices.masterbest.serverside.repo.OrderStatusHistoryRepo;@Servicepublic class OrderHistoryService {    @Autowired    private OrderStatusHistoryRepo repo;    public void saveHistoryItem (User user, Order order, OrderStatus status){        OrderStatusHistory historyItem = new OrderStatusHistory();        historyItem.setUser(user);        historyItem.setOrderStatus(status);        historyItem.setOrder(order);        repo.save(historyItem);    }}