package ru.spb.devpractices.masterbest.serverside.services.model;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Service;import ru.spb.devpractices.masterbest.serverside.exceptions.OrderNotFoundException;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.model.order.Order;import ru.spb.devpractices.masterbest.serverside.repo.OrdersRepo;import java.util.List;@Servicepublic class OrderService {    private final OrdersRepo ordersRepo;    public OrderService(OrdersRepo ordersRepo) {        this.ordersRepo = ordersRepo;    }    public Order getOrder (String ikea_number) {        return ordersRepo.findById(ikea_number).orElseThrow(OrderNotFoundException::new);    }    public Order saveOrder(Order order) {        return ordersRepo.save(order);    }}