package ru.spb.devpractices.masterbest.serverside.services.model;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ru.spb.devpractices.masterbest.serverside.exceptions.OrderTypeNotFoundException;import ru.spb.devpractices.masterbest.serverside.model.order.OrderType;import ru.spb.devpractices.masterbest.serverside.repo.OrderTypesRepo;import java.util.Optional;@Servicepublic class OrderTypesService {    @Autowired    private OrderTypesRepo typesRepo;    public OrderType getOrderTypeByEntity (OrderType type){        Optional<OrderType> typeFromDB = typesRepo.findById(type.getId());        if (typeFromDB.isEmpty()){           return createNewOrderType(type);        }        return typeFromDB.get();    }    public OrderType getOrderTypeById (Long id){        return typesRepo.findById(id).orElseThrow(OrderTypeNotFoundException::new);    }    private OrderType createNewOrderType (OrderType type){        OrderType newType = new OrderType();        newType.setId(type.getId());        newType.setName(type.getName());        return typesRepo.save(newType);    }}