package ru.spb.devpractices.masterbest.serverside.services.model;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Service;import ru.spb.devpractices.masterbest.serverside.exceptions.OrderNotFoundException;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.model.order.Order;import ru.spb.devpractices.masterbest.serverside.repo.OrdersRepo;import java.util.List;@Servicepublic class OrdersService {    private final OrdersRepo ordersRepo;    public OrdersService(OrdersRepo ordersRepo) {        this.ordersRepo = ordersRepo;    }    public Order getOrder (String ikea_number) {        return ordersRepo.findById(ikea_number).orElseThrow(OrderNotFoundException::new);    }    public Order saveOrder(Order order) {        return ordersRepo.save(order);    }    public List<Order> getAllOrdersByUser (User user){        return ordersRepo.getAllByUsersContains(user);    }    public List<Order> getAllByUserId (Long userId){        return ordersRepo.findAllByUser_id(userId);    }}