package ru.spb.devpractices.masterbest.serverside.services.model;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import ru.spb.devpractices.masterbest.serverside.exceptions.UserNotFoundException;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.repo.UsersRepo;import java.util.List;@Servicepublic class UserService {    @Autowired    private UsersRepo usersRepo;    public User getUserById (Long id) {        return usersRepo.findById(id).orElseThrow(UserNotFoundException::new);    }    public User saveUser(User user) {        return usersRepo.save(user);    }    public List<User> getAllUsers () {        return usersRepo.findAll();    }}