package ru.spb.devpractices.masterbest.serverside.services.model;import org.springframework.stereotype.Service;import ru.spb.devpractices.masterbest.serverside.exceptions.UserStatusDataNotFoundException;import ru.spb.devpractices.masterbest.serverside.model.User;import ru.spb.devpractices.masterbest.serverside.model.UserStatusData;import ru.spb.devpractices.masterbest.serverside.repo.UserStatusDataRepo;@Servicepublic class UserStatusDataService {    public final UserStatusDataRepo repo;    public UserStatusDataService(UserStatusDataRepo repo) {        this.repo = repo;    }    public UserStatusData getUserStatusData (Long id){        return repo.findById(id).orElseThrow(UserStatusDataNotFoundException::new);    }    public UserStatusData saveUser(UserStatusData data) {        return repo.save(data);    }}